using Microsoft.AspNetCore;
using Microsoft.AspNetCore.Hosting;

namespace [Namespace]
{
	public class Program
	{
		public static void Main(string[] args)
		{
			BuildWebHost(args).Run();
		}

		public static IWebHost BuildWebHost(string[] args) =>
			WebHost.CreateDefaultBuilder(args)
				.UseStartup<Startup>()
				.Build();
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;

namespace [Namespace].Helpers
{
	public class PropertyCopier
	{
		public static void CopyPropertyValues(object source, object destination)
		{
			var destProperties = destination.GetType().GetProperties();

			foreach (var sourceProperty in source.GetType().GetProperties())
			{
				if (sourceProperty.GetValue(source, new object[] { }) != null)
				{
					foreach (var destProperty in destProperties)
					{
						if (destProperty.Name == sourceProperty.Name &&
					destProperty.PropertyType.IsAssignableFrom(sourceProperty.PropertyType))
						{
							destProperty.SetValue(destination, sourceProperty.GetValue(
								source, new object[] { }), new object[] { });

							break;
						}
					}
				}
			}
		}
	}
}

using EntitySearch;
using Microsoft.EntityFrameworkCore;
using System;
using System.Collections.Generic;
using System.Linq;

namespace [Namespace].Helpers
{
	public class SearchHelper<T> where T : class
	{
		public static IQueryable<T> SearchFiltering(IQueryable<T> source, String field, String searchTerm, String searchOperator)
		{
			Operator soperator = Operator.Equals;
			if (searchTerm != null)
			{
				if (searchTerm.Trim() != "")
				{
					if (field != null)
					{
						if (field.Trim() != "")
						{
							if (searchOperator != null)
							{
								if (searchOperator.Trim() != "")
								{
									switch (searchOperator)
									{
										case "Equals":
											soperator = Operator.Equals;
											break;
										case "LargerThan":
											soperator = Operator.LargerThan;
											break;
										case "LargerEqualThan":
											soperator = Operator.LargerEqualThan;
											break;
										case "LesserThan":
											soperator = Operator.LesserThan;
											break;
										case "LesserEqualThan":
											soperator = Operator.LesserEqualThan;
											break;
										case "StartWith":
											soperator = Operator.StartWith;
											break;
										case "EndsWith":
											soperator = Operator.EndWith;
											break;
										case "Between":
											soperator = Operator.Between;
											break;
										case "Contains":
											soperator = Operator.Contains;
											break;
										default:
											goto NoOperatorSearch;
									}
									goto SearchFieldOperator;
								}
								else
								{
									goto NoOperatorSearch;
								}
							}
							else
							{
								goto NoOperatorSearch;
							}
						}
						else
						{
							if (searchOperator != null)
							{
								if (searchOperator.Trim() != "")
								{
									switch (searchOperator)
									{
										case "Equals":
											soperator = Operator.Equals;
											break;
										case "LargerThan":
											soperator = Operator.LargerThan;
											break;
										case "LargerEqualThan":
											soperator = Operator.LargerEqualThan;
											break;
										case "LesserThan":
											soperator = Operator.LesserThan;
											break;
										case "LesserEqualThan":
											soperator = Operator.LesserEqualThan;
											break;
										case "StartWith":
											soperator = Operator.StartWith;
											break;
										case "EndsWith":
											soperator = Operator.EndWith;
											break;
										case "Between":
											soperator = Operator.Between;
											break;
										case "Contains":
											soperator = Operator.Contains;
											break;
										default:
											goto FullSearch;
									}
									goto NoFieldSearch;
								}
								else
								{
									goto FullSearch;
								}
							}
							else
							{
								goto FullSearch;
							}
						}
					}
					else
					{
						if (searchOperator != null)
						{
							if (searchOperator.Trim() != "")
							{
								goto NoFieldSearch;
							}
							else
							{
								goto FullSearch;
							}
						}
						else
						{
							goto FullSearch;
						}
					}
				}
				else
				{
					goto EndSearch;
				}
			}
			else
			{
				goto EndSearch;
			}

			FullSearch:
				source = source.Where(x => EntitySearch.EntitySearch.Perform(x, searchTerm) == true).AsQueryable();
				goto EndSearch;
			NoOperatorSearch:
				source = source.Where(x => EntitySearch.EntitySearch.Perform(x, field, searchTerm));
				goto EndSearch;
			NoFieldSearch:
				source = source.AsQueryable().Where(x => EntitySearch.EntitySearch.Perform(x, searchTerm, soperator));
				goto EndSearch;
			SearchFieldOperator:
				source = source.AsQueryable().Where(x => EntitySearch.EntitySearch.Perform(x, field, searchTerm, soperator));
				goto EndSearch;
			EndSearch:
			return source;
		}
	}
}

using Microsoft.AspNetCore.Builder;
using Microsoft.AspNetCore.Hosting;
using Microsoft.Extensions.Configuration;
using Microsoft.Extensions.DependencyInjection;
using [Namespace].Models;
using Microsoft.EntityFrameworkCore;

namespace [Namespace]
{
	public class Startup
	{
		public Startup(IConfiguration configuration)
		{
			Configuration = configuration;
		}

		public IConfiguration Configuration { get; }

		// This method gets called by the runtime. Use this method to add services to the container.
		public void ConfigureServices(IServiceCollection services)
		{
			services.AddMvc();
			services.AddOptions();
			services.AddDbContext<[DbName]DB>(options => options.UseSqlServer(Configuration.GetConnectionString("[DbName]DB")));
		}

		// This method gets called by the runtime. Use this method to configure the HTTP request pipeline.
		public void Configure(IApplicationBuilder app, IHostingEnvironment env)
		{
			if (env.IsDevelopment())
			{
				app.UseDeveloperExceptionPage();
			}

			app.UseMvc();
		}
	}
}
